import "./style/SlideShow.css"

import { useState } from "react"

const SlideShow = ({ images }) => {
    const [currSlideIndex, setCurrSlideIndex] = useState(0)

    const setIndex = (num) => {
        const i = currSlideIndex + num
        if (i > images.length - 1) {
            setCurrSlideIndex(0)
        } else if (i < 0) {
            setCurrSlideIndex(images.length - 1)
        } else {
            setCurrSlideIndex(i)
        }
    }

    return (
        <div className="slideshow-container">
            <div className="slides fade">
                <img src={images[currSlideIndex].url} />
                <div className="arrows">
                    <a className="prev" onClick={() => setIndex(-1)}>&#10094;</a>
                    <a className="next" onClick={() => setIndex(1)}>&#10095;</a>
                </div>
                <div className="caption">{images[currSlideIndex].caption}</div>
            </div>
        </div>
    )
}

export default SlideShow;
